import React, { useState, useEffect } from "react";

const EnemyName = ({ client, channel }) => {
    const [enemyName, setEnemyName] = useState("Unknown");
  
    useEffect(() => {
      // Fetch the name of the other user in the channel
      const otherUsers = channel.state.members.filter(
        (member) => member.user.id !== client.user.id
      );
      if (otherUsers.length > 0) {
        const enemy = otherUsers[0];
        setEnemyName(enemy.user.name);
      } else {
        setEnemyName("No enemy found");
      }
    }, [channel.state.members, client.user.id]);
  
    return (
      <div style={{ position: "absolute", top: 0, right: 0 }}>
        Enemy: {enemyName}
      </div>
    );
  };
  export default EnemyName;
