import React, {useState} from 'react'
import Login from './components/Login';
import SignUp from './components/SignUp';

function Form({setIsAuth}) {
    const [showSignUp, setShowSignUp] = useState(false);
  
    const handleSignUpClick = () => {
      setShowSignUp(true);
    };


  return (
    <div className='bg-white px-10 py-20 rounded-3xl border-2 border-gray-200'>
        <h1 className='text-5xl font-semibold'>Welcome Back</h1>
        <p className='font-medium text-lg text-gray-500 mt-4'>Welcome back! Please enter your details.</p>
        <SignUp setIsAuth={setIsAuth} />
        <Login setIsAuth={setIsAuth} />
        <div className='mt-8 flex justify-center items-center'>
        <p className='font-medium text-base'>Dont have an account?</p>
        <button onClick={handleSignUpClick} className='text-violet-500 text-base font-medium ml-2'>Sign Up</button>
        </div>
        {showSignUp && <SignUp setIsAuth={setIsAuth} />}
    </div>
  )
}

export default Form
