import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useChatContext } from 'stream-chat-react';
import './WinLoss.css'
function WinLoss({ }) {
  const [wins, setWins] = useState(0);
  const [losses, setLosses] = useState(0);
  const [error, setError] = useState(null);
  const { client } = useChatContext();
  const userId = client.user?.id;
  

  useEffect(() => {
    axios.get(`http://localhost:3001/get-UserStats?userId=${userId}`)
      .then(res => {
        setWins(res.data.success.wins);
        setLosses(res.data.success.losses);
      })
      .catch(err => {
        console.log(err);
        setError('Error loading user stats');
      });
  }, [userId]);


  return (
    <div>
      <h1 className='wl'>Your W/L:</h1>
      <p className='win'>Wins: {wins}</p>
      <p className='loose'>Losses: {losses}</p>
    </div>
  );
}

export default WinLoss;
