import React from 'react';
import { useChatContext } from 'stream-chat-react';

const Win = ({channel}) => {
  const [winner, setWinner] = useState(null);
  const { client } = useChatContext();

  if (!channel) {
    return <div>Loading...</div>;
  }

  useEffect(() => {
    const fetchWinner = async () => {
      const members = Object.values(channel.state.members);
      const winnerMember = members.find((member) => member.user.id === winnerUserId);
      setWinner(winnerMember?.user.name || 'Unknown');
    };

    if (winnerUserName && channel) {
      fetchWinner();
    }
  }, [winnerUserName, channel]);

  const winMessage = channel.state.messages[channel.state.messages.length - 1];
  const winnerUserId = winMessage.winnerUserId;
  const winnerTempWord = winMessage.winnerTempWord;

  return (
    <div className='win'>
      <h1>Game finished!</h1>
      <p>{winnerUserId} {winner} won with the word "{winnerTempWord}"</p>

    </div>
  );
};

export default Win;
