import React, { useContext, useState, Wordcontext } from 'react'
import './tailwind.css'

export const Inputcontext = createContext();

function WordInput({visible, onClose}) {

  const [inputValue, setInputValue] = useState("");


  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  }


    const handleKeyDown = (event) => {
      if (event.key === 'Enter') {
        onClose()
      }
    }

    if(!visible) return null;
    console.log(inputValue)
  return (

    <div className='fixed inset-0 bg-black bg-opacity-30 backdrop-blur-sm flex justify-center items-center '>
    <Wordcontext.Provider value={{ inputValue }}>
        <div className='w-[600px] flex flex-col'>
            {/*<button className='text-white text-xl place-self-end' onClick={() => onClose()}>X</button> */}
            <div className='bg-white p-2 rounded'>Write an Wordle Word for your Enemy:</div>
            <label htmlFor="input">
              <input id ="input" type="text" value={inputValue} onChange={handleInputChange} onKeyDown={handleKeyDown}></input>
            </label>
            <p>Here:{inputValue}</p>
        </div>
      </Wordcontext.Provider>
    </div>
  )
}

export default WordInput
