import React, { useState, createContext  } from 'react'
import { Window, MessageList, MessageInput } from 'stream-chat-react'
import './Chat.css';
import MainGame from './Maingame';
import './Components.css';
import Board from './Board';
import Keyboard from './Keyboard';
import { boardDefault } from './Words';



export const Gamecontext = createContext();

function Game({channel}) {
  //wordle
  const [board, setBoard] = useState(boardDefault);
  const [currAttempt, setCurrAttempt] = useState({attemp: 0, letterPos: 0});

  const [playersJoined, setPlayersJoined] = useState(channel.state.watcher_count === 2);

channel.on("user.watching.start", (event) => {
  setPlayersJoined(event.watcher_count === 2);
});

// winner
const [result, setResult] = useState({winner: "none", state:"none"})
if (!playersJoined){
        return <div>Waiting for other player to join</div>
    }
  return (
    <div className='gameContainer'>

      {/* <MainGame result ={result} setResult={setResult}/> */}
      <Gamecontext.Provider value={{ board, setBoard}}>
      <div className='game'>
      <Board/>
      <Keyboard/>
      </div>
      </Gamecontext.Provider>
      <Window>
       <MessageList disableDateSeparator closeReactionSelectorOnClick messageActions={["react"]} hideDeletedMessages/>
       <MessageInput noFiles/>
      </Window>
    </div>
  )
}

export default Game
