import React, { useState, useEffect } from "react";

const EnemyName = ({ client, channel }) => {
  const [enemyName, setEnemyName] = useState("");

  useEffect(() => {
    // Fetch the name of the other user in the channel
    let otherUsers = [];
    if (Array.isArray(channel.state.members)) {
      otherUsers = channel.state.members.filter(
        (member) => member.user.id !== client.user.id
      );
    }
    const enemy = otherUsers[0];
    setEnemyName(enemy ? enemy.user.name : "");
  }, [channel.state.members, client.user.id]);

  return (
    <div style={{ position: "absolute", top: 0, right: 0 }}>
      Enemy: {enemyName}
    </div>
  );
};

export default EnemyName;
