import React, {useState} from 'react'
import Axios from "axios";
import Cookies from "universal-cookie"
import '../App.css';

function SignUp({setIsAuth}) {
    const cookies = new Cookies();
    const [user, setUser] = useState(null)

    // Function to sign up a new user
    const signUp = () => {
        Axios.post("http://localhost:3001/signup", user).then((res) => {
            // Extracting user data from the response
            const { token, userId, firstName, lastName, username, hashedPassword} = res.data;
            // Setting user data in cookies for future use
            cookies.set("token", token);
            cookies.set("userId", userId);
            cookies.set("username", username);
            cookies.set("lastName", lastName);
            cookies.set("firstName", firstName);   
            cookies.set("hashedPassword", hashedPassword);
            // Setting authentication status to true
            setIsAuth(true);
        });
    };
  return (
    <div className='signup mt-8'>
        <label className='signup__label text-lg font-medium' >FirstName</label>

        <input className='signup__input -full border-2 border-gray-100 rounded-xl p-4 mt-1 bg-transparent'  placeholder='First Name' onChange={(event) =>{
            setUser({ ...user, firstName: event.target.value});
        }}></input>
        <label className='signup__label text-lg font-medium' >LastName</label>
        <input className='signup__input -full border-2 border-gray-100 rounded-xl p-4 mt-1 bg-transparent' placeholder='Last Name' onChange={(event) =>{
            setUser({ ...user, lastName: event.target.value});
        }}></input>
        <label className='signup__label text-lg font-medium' >Username</label>
        <input className='signup__input -full border-2 border-gray-100 rounded-xl p-4 mt-1 bg-transparent' placeholder='Username' onChange={(event) =>{
            setUser({ ...user, username: event.target.value});
        }}></input>
        <label className='signup__label text-lg font-medium' >Password</label>
        <input className='signup__input -full border-2 border-gray-100 rounded-xl p-4 mt-1 bg-transparent' placeholder='Password' onChange={(event) =>{
            setUser({ ...user, password: event.target.value});
        }}></input>
        <div className='mt-8 flex flex-col gap-y-4'>
            <button className='signup__button active:scale-[.98] active:duration-75 hover:scale-[1.01] ease-in-out transition-all py-3 px-8 rounded-xl bg-violet-500 text-white text-lg font-bold'>Sign Up</button>
        </div>
    </div>
  )
}

export default SignUp
