import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Enemy({ userId }) {
  const [enemyStats, setEnemyStats] = useState(null)

  useEffect(() => {
    const getUserStats = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/userStats?userId=${userId}`)
        const { data } = response.data
        setEnemyStats(data)
      } catch (error) {
        console.log(error)
      }
    }

    getUserStats()
  }, [userId])

  return (
    <div>
      <h2>Enemy Stats</h2>
      {enemyStats && (
        <div>
          <p>Wins: {enemyStats.wins}</p>
          <p>Losses: {enemyStats.losses}</p>
        </div>
      )}
    </div>
  )
}

export default Enemy
