import React, {useCallback, useEffect, useContext, useSyncExternalStore, useState} from 'react'
import './Components.css';
import Key from './Key';
import { Gamecontext } from './Game';

function Keyboard(visible, showWordInput) {
  const keys1 = ["Q", "W", "E","R","T","Y","U", "I", "O","P"];
  const keys2 = ["A","S","D","F","G","H","J","K","L"];
  const keys3 = ["Z","X","C","V","B","N","M"];
const { onDelete, onSelectLetter, onEnter, setShowWordInput} = useContext(Gamecontext);

  const handleKeyboard = useCallback((event) =>{
    if (event.key === "Enter"){
      onEnter();

    } else if (event.key === "Backspace"){
      onDelete();
    }
    else {
      keys1.forEach((key) => {
        if (event.key.toLowerCase() ===key.toLowerCase() ) {
          onSelectLetter(key)
        }
      })
      keys2.forEach((key) => {
        if (event.key.toLowerCase()  ===key.toLowerCase() ) {
          onSelectLetter(key)
        }
      })
      keys3.forEach((key) => {
        if (event.key.toLowerCase()  ===key.toLowerCase() ) {
          onSelectLetter(key)
        }
      })
    }
  })
  // got to get that if modal is open removeevventlistener otherwise addeventlistener


    if(setShowWordInput === false){
      document.addEventListener("keydown", handleKeyboard)
    }
    else{
      document.removeEventListener("keydown", handleKeyboard)
    }














  return( <div className='keyboard' onKeyDown={handleKeyboard}>
    <div className='line1'>{keys1.map(key =>{
      return <Key keyVal={key} />;
    })}
    </div>
    <div className='line2'>{keys2.map(key =>{
      return <Key keyVal={key} />;
    })}
    </div>
    <div className='line3'>
      <Key keyVal={"ENTER"} bigKey />
      {keys3.map(key =>{
      return <Key keyVal={key} />;
    })}
      <Key keyVal={"DELETE"} bigKey />
    </div>
  </div>
  );
  
}

export default Keyboard;
