import { useState, useEffect } from "react";

function useCheckWord() {
  const [checkWin, setCheckWin] = useState(false);

  useEffect(() => {
    const targetSelector = ".str-chat__ul";
    let targetNode = document.querySelector(targetSelector);
  
    const handleDOMNodeInserted = (event) => {
      if (event.target.matches(targetSelector)) {
        targetNode = event.target;
        observer.observe(targetNode, { childList: true });
      }
    };
  
    if (targetNode) {
      const observer = new MutationObserver((mutationsList, observer) => {
        // Handle mutations
      });
  
      observer.observe(targetNode, { childList: true });
  
      document.addEventListener("DOMNodeInserted", handleDOMNodeInserted);
  
      return () => {
        observer.disconnect();
        document.removeEventListener("DOMNodeInserted", handleDOMNodeInserted);
      };
    }
  }, []);
  

  return checkWin;
}

export default useCheckWord;
