import React, { useState, useEffect, createContext } from 'react'
import {useChatContext, Channel} from 'stream-chat-react'
import CustomInput from './CustomInput';
import Game from './Game';
import { generateWordSet } from './Words';

export const JoinContext = createContext();

function JoinGame() {
  const [rivalUsername, setRivalUsername] = useState("");
  const { client } = useChatContext();
  const [channel, setchannel] = useState(null)
  const [selectedWord, setSelectedWord] = useState("");
  const [wordSet, setWordSet] = useState(new Set())


  useEffect(() => {
    generateWordSet().then((words) => {
      setWordSet(words.wordSet);
      setSelectedWord(words.todaysWords);
    });
  }, [ ]);



  const createChannel = async () => {
  const response = await client.queryUsers({ name: { $eq: rivalUsername}});


    if (response.users.length === 0) {
      alert("User not found")
      return
    }

    const newChannel = await client.channel("messaging", {
      members: [client.userID, response.users[0].id],
    });

    await newChannel.watch();
    setchannel(newChannel);
  };
  return (
    <>
    <JoinContext.Provider value={selectedWord}>


    {channel ? (
      <Channel channel={channel} Input={CustomInput}>
      {channel && <Game channel={channel} selectedWord={selectedWord}/>}
      </Channel>
    )  : (
    
    
    <div>
    <h4>Create Game</h4>
    <input placeholder="Username of rival" onChange={(event) => {
        setRivalUsername(event.target.value);
    }}
     />
     <button onClick={createChannel}> Join game</button>
    </div>
  )}
  </JoinContext.Provider>
  </>
  )
}

export default JoinGame
